import { useState } from "react";

function RatingSelector(props) {
let [ratingRange,setRatingRange]=useState([1,2,3,4,5]);

let handleClick=(rating)=> {
    //console.log(rating)
    props.passRatingToParent(rating);       // we pass the value to parent component 
}
    return(
        <div>
            <button type="button">Click</button>
            <h4>Select Your Rating</h4>
            {
                ratingRange.map(rating=>
                    <button type="button" onClick={()=>handleClick(rating)} key={rating}>

                {rating}
                <img src="data:image/png;base64,"
                width="30px" height="30px"/>

                </button>
                )
            }
        </div>
    )
}

export default RatingSelector;